def size
	puts "Il y a #{@data.size} journalistes"
end


def number
	number = @data.count{|str| str.match(/[0-9]/)}
	puts "Il y a #{number} handles qui contiennent un numéro"
end

def aude
	aude = @data.count{|str| str.downcase.include?("aude")}
	puts "Il y a #{aude} handles contenant 'aude'"
end

def majbegin
	majbegin = @data.select{|str| str[1].match(/[A-Z]/)}.size
	puts "Il y a #{majbegin} handles qui commencent par une majuscule"
end


def maj
	maj = @data.count{|str| str.match(/[A-Z]/)}
	puts "Il y a #{maj} handles qui contiennent une majuscule"
end

def underscore
	under = 0 
	underscore = @data.select{|str| under+= str.count("_")}
	puts "Il y a #{under} '_'"
end

def order
	puts @data.sort
end



def user
	choice = 0
	while choice != 99

		puts "Que veux-tu faire ?"
		puts "1 - Afficher le nombre de journalistes"
		puts "2 - Afficher le nombre de handles qui contiennent un numéro"
		puts "3 - Afficher le nombre de handles qui contiennent aude"
		puts "4 - Afficher le nombre de handles qui commencent par une majuscule"
		puts "5 - Afficher le nombre de handles qui contiennent une majuscule"
		puts "6 - Afficher le nombre total de _"
		puts "7 - Trier la liste par ordre alphabétique"
		puts "99 - Quitter"
		print "> "
		    choice = gets.chomp.to_i
		    puts "--------------------------"
			    if choice == 1
				    size
			    elsif choice == 2
			        number
			    elsif choice == 3
			    	aude
			    elsif choice == 4
			    	majbegin
			    elsif choice == 5
			    	maj
			    elsif choice == 6
			    	underscore
			    elsif choice == 7
			    	order
			    end



   			 puts "--------------------------"
	end


end

